import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import PhoneService from '../../services/PhoneService';
import checkmark from '../../utils/checkmark';
import './PhoneDetail.css';

const PhoneDetail = () => {
  const { phoneId } = useParams();
  const [phone, setPhone] = useState(null);
  const [mainImageUrl, setMainImageUrl] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPhone = async () => {
      setLoading(true);
      try {
        console.log(`Fetching phone with ID: ${phoneId}`);
        const data = await PhoneService.getPhone(phoneId);
        console.log(`Phone ${phoneId} data received:`, data);

        if (data) {
          setPhone(data);
          if (data.images && data.images.length > 0) {
            setMainImageUrl(data.images[0]);
          }
          setError(null);
        } else {
          setError('Phone not found');
        }
      } catch (err) {
        console.error('Error fetching phone details:', err);
        setError('Failed to load phone details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchPhone();
  }, [phoneId]);

  const setImage = (imageUrl) => {
    setMainImageUrl(imageUrl);
  };

  // Fix image URLs to point to assets
  const getImageUrl = (imageUrl) => {
    if (!imageUrl) return '/assets/img/phones/placeholder.jpg';
    // Convert imageUrl to proper path
    return `/assets/${imageUrl}`;
  };

  if (loading) {
    return <div className="loading">Loading phone details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!phone) {
    return <div className="error">Phone not found</div>;
  }

  return (
    <div className="phone-detail">
      <div className="phone-images">
        {mainImageUrl && (
          <img 
            src={getImageUrl(mainImageUrl)} 
            alt={phone.name} 
            className="phone selected" 
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = 'https://via.placeholder.com/400x400?text=Phone+Detail';
            }}
          />
        )}
      </div>

      <h1>{phone.name}</h1>

      <p>{phone.description}</p>

      <ul className="phone-thumbs">
        {phone.images && phone.images.map((img, index) => (
          <li key={index}>
            <img 
              src={getImageUrl(img)} 
              alt={`${phone.name} thumbnail ${index}`}
              onClick={() => setImage(img)}
              className={img === mainImageUrl ? 'selected' : ''}
              onError={(e) => {
                e.target.onerror = null;
