            <dd>{phone.camera && phone.camera.primary}</dd>
            <dt>Features</dt>
            <dd>{phone.camera && phone.camera.features && phone.camera.features.join(', ')}</dd>
          </dl>
        </li>
        <li>
          <span>Additional Features</span>
          <dd>{phone.additionalFeatures}</dd>
        </li>
      </ul>
    </div>
  );
