import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import PhoneService from '../../services/PhoneService';
import checkmark from '../../utils/checkmark';
import './PhoneDetail.css';

const PhoneDetail = () => {
  const { phoneId } = useParams();
  const [phone, setPhone] = useState(null);
  const [mainImageUrl, setMainImageUrl] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPhone = async () => {
      setLoading(true);
      try {
        const data = await PhoneService.getPhone(phoneId);
        setPhone(data);
        if (data && data.images && data.images.length > 0) {
          setMainImageUrl(data.images[0]);
        }
        setError(null);
      } catch (err) {
        console.error('Error fetching phone details:', err);
        setError('Failed to load phone details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchPhone();
  }, [phoneId]);

  const setImage = (imageUrl) => {
    setMainImageUrl(imageUrl);
  };

  // Fix image URLs to point to assets
  const getImageUrl = (url) => {
    if (!url) return '';
    // Replace img/ with /assets/img/ to match our public folder structure
    return url.replace('img/', '/assets/img/');
  };

  if (loading) {
    return <div className="loading">Loading phone details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!phone) {
    return <div className="error">Phone not found</div>;
  }

  return (
    <div className="phone-detail">
      <div className="phone-images">
        {mainImageUrl && (
          <img src={getImageUrl(mainImageUrl)} alt={phone.name} className="phone selected" />
        )}
      </div>

      <h1>{phone.name}</h1>

      <p>{phone.description}</p>

      <ul className="phone-thumbs">
        {phone.images.map((img, index) => (
          <li key={index}>
            <img 
              src={getImageUrl(img)} 
              alt={`${phone.name} thumbnail ${index}`}
              onClick={() => setImage(img)}
              className={img === mainImageUrl ? 'selected' : ''}
            />
          </li>
        ))}
      </ul>

      <ul className="specs">
        <li>
          <span>Availability and Networks</span>
          <dl>
            <dt>Availability</dt>
            {phone.availability.map((availability, index) => (
              <dd key={index}>{availability}</dd>
            ))}
          </dl>
        </li>
        <li>
          <span>Battery</span>
          <dl>
            <dt>Type</dt>
            <dd>{phone.battery.type}</dd>
            <dt>Talk Time</dt>
            <dd>{phone.battery.talkTime}</dd>
            <dt>Standby time (max)</dt>
            <dd>{phone.battery.standbyTime}</dd>
          </dl>
        </li>
        <li>
          <span>Storage and Memory</span>
          <dl>
            <dt>RAM</dt>
            <dd>{phone.storage.ram}</dd>
            <dt>Internal Storage</dt>
            <dd>{phone.storage.flash}</dd>
          </dl>
        </li>
        <li>
          <span>Connectivity</span>
          <dl>
            <dt>Network Support</dt>
            <dd>{phone.connectivity.cell}</dd>
            <dt>WiFi</dt>
            <dd>{phone.connectivity.wifi}</dd>
            <dt>Bluetooth</dt>
            <dd>{phone.connectivity.bluetooth}</dd>
            <dt>Infrared</dt>
            <dd dangerouslySetInnerHTML={{ __html: checkmark(phone.connectivity.infrared) }} />
            <dt>GPS</dt>
            <dd dangerouslySetInnerHTML={{ __html: checkmark(phone.connectivity.gps) }} />
          </dl>
        </li>
        <li>
          <span>Android</span>
          <dl>
            <dt>OS Version</dt>
            <dd>{phone.android.os}</dd>
            <dt>UI</dt>
            <dd>{phone.android.ui}</dd>
          </dl>
        </li>
        <li>
          <span>Size and Weight</span>
          <dl>
            <dt>Dimensions</dt>
            {phone.sizeAndWeight.dimensions.map((dim, index) => (
              <dd key={index}>{dim}</dd>
            ))}
            <dt>Weight</dt>
            <dd>{phone.sizeAndWeight.weight}</dd>
          </dl>
        </li>
        <li>
          <span>Display</span>
          <dl>
            <dt>Screen size</dt>
            <dd>{phone.display.screenSize}</dd>
            <dt>Screen resolution</dt>
            <dd>{phone.display.screenResolution}</dd>
            <dt>Touch screen</dt>
            <dd dangerouslySetInnerHTML={{ __html: checkmark(phone.display.touchScreen) }} />
          </dl>
        </li>
        <li>
          <span>Hardware</span>
          <dl>
            <dt>CPU</dt>
            <dd>{phone.hardware.cpu}</dd>
            <dt>USB</dt>
            <dd>{phone.hardware.usb}</dd>
            <dt>Audio / headphone jack</dt>
            <dd>{phone.hardware.audioJack}</dd>
            <dt>FM Radio</dt>
            <dd dangerouslySetInnerHTML={{ __html: checkmark(phone.hardware.fmRadio) }} />
            <dt>Accelerometer</dt>
            <dd dangerouslySetInnerHTML={{ __html: checkmark(phone.hardware.accelerometer) }} />
          </dl>
        </li>
        <li>
          <span>Camera</span>
          <dl>
            <dt>Primary</dt>
            <dd>{phone.camera.primary}</dd>
            <dt>Features</dt>
            <dd>{phone.camera.features.join(', ')}</dd>
          </dl>
        </li>
        <li>
          <span>Additional Features</span>
          <dd>{phone.additionalFeatures}</dd>
        </li>
      </ul>
    </div>
  );
};

export default PhoneDetail;
