import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PhoneService from '../../services/PhoneService';
import './PhoneList.css';

// Placeholder image for phones
const PLACEHOLDER_IMAGE = 'https://via.placeholder.com/100x100?text=Phone';

const PhoneList = () => {
  // State used to replace the AngularJS controller
  const [phones, setPhones] = useState([]);
  const [query, setQuery] = useState('');
  const [orderProp, setOrderProp] = useState('age');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch phones data on component mount
  useEffect(() => {
    const fetchPhones = async () => {
      setLoading(true);
      try {
        console.log('Fetching phones...');
        const data = await PhoneService.getPhones();
        console.log('Phones data received:', data);

        // Ensure data is an array
        if (Array.isArray(data)) {
          setPhones(data);
        } else {
          console.error('Phones data is not an array:', data);
          setError('Invalid phone data format. Please try again later.');
          setPhones([]);
        }
      } catch (err) {
        console.error('Error fetching phones:', err);
        setError('Failed to load phones. Please try again later.');
        setPhones([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPhones();
  }, []);

  // Filter phones based on search query - with safety check
  const filteredPhones = Array.isArray(phones) 
    ? phones.filter((phone) => {
        const searchString = `${phone.name} ${phone.snippet}`.toLowerCase();
        return searchString.includes(query.toLowerCase());
      })
    : [];

  // Sort phones based on selected order
  const sortedPhones = [...filteredPhones].sort((a, b) => {
    if (orderProp === 'name') {
      return a.name.localeCompare(b.name);
    } else {
      return a[orderProp] - b[orderProp];
    }
  });

  // Fix image URLs to point to assets or use placeholder
  const getImageUrl = (url) => {
    // Return placeholder image since we don't have the original images
    return PLACEHOLDER_IMAGE;
  };

  if (loading) {
    return <div className="loading">Loading phones...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-2">
          {/* Sidebar content */}
          <p>
            Search:
            <input
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="form-control"
            />
          </p>

          <p>
            Sort by:
            <select
              value={orderProp}
              onChange={(e) => setOrderProp(e.target.value)}
              className="form-control"
            >
              <option value="name">Alphabetical</option>
              <option value="age">Newest</option>
            </select>
          </p>
        </div>

        <div className="col-md-10">
          {/* Body content */}
          <ul className="phones">
            {sortedPhones.length > 0 ? (
              sortedPhones.map((phone) => (
                <li key={phone.id} className="thumbnail phone-list-item">
                  <Link to={`/phones/${phone.id}`} className="thumb">
                    <img src={getImageUrl(phone.imageUrl)} alt={phone.name} />
                  </Link>
                  <Link to={`/phones/${phone.id}`}>{phone.name}</Link>
                  <p>{phone.snippet}</p>
                </li>
              ))
            ) : (
              <p>No phones found</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default PhoneList;
